w3 = Web3(Web3.HTTPProvider('https://rpc-mainnet.kcc.network'))
# router = "0x6074e20633d2d8fbdf82119867a81581cabe06dd,0xa58350d6dee8441aa42754346860e3545cc83cda".split(",")
router = os.environ['ROUTERS'].split(",")
routerContracts = [w3.eth.contract(address=Web3.toChecksumAddress(r), abi=router_abi) for r in router]
print(router)

last_message_id = {}
last_price_message_id = {}

kafe = Web3.toChecksumAddress("0x516F50028780B60e2FE08eFa853124438f9E46a7")
kcs = Web3.toChecksumAddress("0x4446fc4eb47f2f6586f9faab68b3498f86c07521")

kafeContract = w3.eth.contract(address=Web3.toChecksumAddress(kafe), abi=erc20_abi)
burn_address = Web3.toChecksumAddress("0x000000000000000000000000000000000000dEaD")

def getKcsPrice():
    response = requests.get("https://api.coingecko.com/api/v3/simple/price?ids=kucoin-shares&vs_currencies=usd")
    return response.json()['kucoin-shares']['usd']

def getKafePrice():
    prices = [r.functions.getAmountsOut(
        w3.toWei(1, 'ether'), [kcs,kafe]).call() for r in routerContracts]
    prices = [p[1] for p in prices]
    avg = sum(prices) / len(prices)
    finalPrice = getKcsPrice() * (1e18 / avg) 
    return finalPrice

def getPrice(update, context):
    # price
    finalPrice = getKafePrice()

    # totalSupply
    total = kafeContract.functions.totalSupply().call()
    burnt = kafeContract.functions.balanceOf(burn_address).call()
    circ = (total - burnt)/1e18
    circMcap = circ * finalPrice

    # find old msg and delete
    if (update.message.chat_id in last_message_id):
        context.bot.deleteMessage(update.message.chat_id, last_message_id[update.message.chat_id])
        context.bot.deleteMessage(update.message.chat_id, last_price_message_id[update.message.chat_id])

    test = context.bot.send_message(chat_id=update.message.chat_id, text=f"""
☕☕$KAFE☕☕
Price: ${finalPrice:.2f}
Circ Supply: {round(circ):,}
Circ Mcap: ${round(circMcap):,}
""")
    last_message_id[update.message.chat_id] = test['message_id']
    last_price_message_id[update.message.chat_id] = update.message.message_id





start_handler = CommandHandler('price', getPrice)
dispatcher.add_handler(start_handler)


